import React, { useEffect, useState } from "react";

const HospForm = ({ active, handleModal, token, id, setErrorMessage }) => {
  const [firstName, setFirstName] = useState("");
  const [lastName, setLastName] = useState("");
  const [maidenName, setMaidenName] = useState("");

  const [birthdayDate, setBirthdayDate] = useState("");

  const [email, setEmail] = useState("");

  const [numberAddress, setNumberAddress] = useState("")
  const [nameAddress, setNameAddress] = useState("")
  const [postalCode, setPostalCode] = useState("")
  const [cityAddress, setCityAddress] = useState("")
  const [countryAddress, setCountryAddress] = useState("")

  const [secuNumber, setSecuNumber] = useState("")

  const [hospitalName, setHospitalName] = useState("")
  const [causeHosp, setCauseHosp] = useState("")


  useEffect(() => {
    const getPatientForm = async () => {
      const requestOptions = {
        method: "GET",
        headers: {
          "Content-Type": "application/json",
          Authorization: "Bearer " + token,
        },
      };
      const response = await fetch(`/hospForm/form/${id}`, requestOptions);

      if (!response.ok) {
        setErrorMessage("Questionnaire non disponible !");
      } else {
        const data = await response.json();
        setFirstName(data.first_name);
        setLastName(data.last_name);
        setMaidenName(data.maiden_name);

        setBirthdayDate(data.birthday_date);

        setEmail(data.email);

        setNumberAddress(data.number_address);
        setNameAddress(data.name_address);
        setPostalCode(data.postal_code);
        setCityAddress(data.city_address);
        setCountryAddress(data.country_address);

        setSecuNumber(data.secu_number);

        setHospitalName(data.hospital_name);
        setCauseHosp(data.cause_hosp);
      }
    };

    if (id) {
      getPatientForm();
    }
  }, [id, token]);

  const cleanFormData = () => {
    setFirstName("");
    setLastName("");
    setMaidenName("");

    setBirthdayDate("");

    setEmail("");

    setNumberAddress("");
    setNameAddress("");
    setPostalCode("");
    setCityAddress("");
    setCountryAddress("");

    setSecuNumber("");

    setHospitalName("");
    setCauseHosp("");

  };

  const handleCreateLead = async (e) => {
    e.preventDefault();
    const requestOptions = {
      method: "POST",
      headers: {
        "Content-Type": "application/json",
        Authorization: "Bearer " + token,
      },
      body: JSON.stringify({
        first_name: firstName,
        last_name: lastName,
        maiden_name: maidenName,

        birthday_date: birthdayDate,

        email: email,

        number_address: numberAddress,
        name_address: nameAddress,
        postal_code: postalCode,
        city_address: cityAddress,
        country_address: countryAddress,

        secu_number: secuNumber,

        hospital_name: hospitalName,
        cause_hosp: causeHosp,
      }),
    };
    const response = await fetch("/hospForm/form", requestOptions);
    if (!response.ok) {
      setErrorMessage("Une erreur à été détecté lors de la création du formulaire");
    } else {
      cleanFormData();
      handleModal();
    }
  };

  const handleUpdateLead = async (e) => {
    e.preventDefault();
    const requestOptions = {
      method: "PUT",
      headers: {
        "Content-Type": "application/json",
        Authorization: "Bearer " + token,
      },
      body: JSON.stringify({
        first_name: firstName,
        last_name: lastName,
        maiden_name: maidenName,

        birthday_date: birthdayDate,

        email: email,

        number_address: numberAddress,
        name_address: nameAddress,
        postal_code: postalCode,
        city_address: cityAddress,
        country_address: countryAddress,

        secu_number: secuNumber,

        hospital_name: hospitalName,
        cause_hosp: causeHosp,
      }),
    };
    const response = await fetch(`/hospForm/form/${id}`, requestOptions);
    if (!response.ok) {
      setErrorMessage("Une erreur à été détecté lors du chargement du formulaire");
    } else {
      cleanFormData();
      handleModal();
    }
  };

  return (
    <div className={`modal ${active && "is-active"}`}>
